import React, { Component } from 'react';
import { Redirect } from 'react-router-dom';
import './style.css';

class Vip extends Component {

	constructor(props) {
		super(props);
		this.state={
			login: true
		}
	}

	render() {
		if (this.state.login) {
			return(
				<div className='vip'>VIP</div>
			)	
		}else{
			return <Redirect to='/' />
		}
	}

	componentDidMount() {
		this.axios.get('http://www.dell-lee.com/react/api/isLogin.json', {
			withCredentials: true
		})
			.then(res => {
				const login = res.data.data.login;
				this.setState({ login })
			})
	}
}

export default Vip;
